def sequence_stats(fasta):
    """
    :param fasta: *.fasta-file, str
    :return: statistics, str
    """

    taxa_name = fasta.split(' ')[0][1:]
    seq = fasta.split(' ')[1]

    stats = {'A': seq.count('A'),
             'T': seq.count('T'),
             'G': seq.count('G'),
             'C': seq.count('C')
             }
    length = len(seq)

    gc_content = (stats['G'] + stats['C']) / length
    at_content = 1 - gc_content
    purines = stats['A'] + stats['G']
    pyrimidines = length - purines

    return (((f'\tGC-content: {gc_content:.3f}\n\
    AT-content: {at_content:.3f}\n\
    nucleotides total: {length}\n\
    purines: {purines}\n\
    pyrimidines: {pyrimidines}\n\n\
    {taxa_name}: A: ' + str(stats['A']) +
              '; T: ' + str(stats['T'])) +
             '; G: ' + str(stats['G'])) +
            '; C: ' + str(stats['C']))


print(sequence_stats('>NM_001127233.1 '))
